#! node
'use strict';

var program = require('commander');
var fs = require('fs');
var process = require('child_process');
program
    .command('init')//声明hi下有一个命令叫list
    .action(function () {
        process.exec('mkdir my-react-pro',function () {
            process.exec('cd my-react-pro',function () {
                fs.readFile("bb.txt","utf8",function (error,data){
                    if(error) throw error ;
                    console.log(data) ;
                }) ;
            })
        })
    })
